import React,{useContext, useEffect, useState} from 'react';
import signup1 from './signup1.jpg';
import { UserContext } from '../../UserContext';
import {Redirect} from 'react-router-dom';
const Login = () => {
	const {user, setUser} = useContext(UserContext);
	const [email, setEmail] = useState('');
	const [password, setPassword] = useState('')
	const [emailError, setEmailError] = useState('');
	const [passwordError, setPasswordError] = useState('');
	const submitHandler=async e=>{
		e.preventDefault();
		setEmailError('');
		setPasswordError('');
		console.log(email,password)
		try{
			const res = await fetch('http://localhost:5000/login',{
				method: 'POST',
				credentials: 'include',
				body: JSON.stringify({email,password}),
				headers:{'Content-Type': 'application/json'}
			});
			const data = await res.json();
			console.log(data);
			if(data.errors){
				setEmailError(data.errors.email);
				setPasswordError(data.errors.password);
			}
			if(data.user){
				setUser(data.user)
			}
		}catch(error){
			console.log(error);
		}
		}

	if(user){
		return <Redirect to="/"/>
	}
	return (
		<div className="row white">
			<form className="form col s6" onSubmit={submitHandler}>
				<div className="row">
					<div className="input-field col s8">
						<input placeholder="Enter Your valid EmailID" id="email" type="email" className="validate black-text" />
						<label htmlFor="email">Email</label>
					</div>
				</div>
				<div className="row">
					<div className="input-field col s8">
						<input placeholder="Minimum 6 Digits" id="password" type="password" className="validate black-text" />
						<label htmlFor="password">Password</label>
					</div>
				</div>


				<button className="btn btn-left orange darken-2">LOGIN</button>
			</form>
			<div>

			<img src={signup1} alt="login" className="login-image" />
			</div>
		</div>

	)
}

export default Login;
