import React from 'react';
import signup2 from './signup2.jpg';
import {Link} from 'react-router-dom';
import { Component } from 'react';
class Signup extends Component{
	constructor(){
		super();
		this.state={
			name:"",
			email:"",
			password:"",
			bio:"",
			jobtitle:"",
			techdomain:"",
			gender:"",
			dateofbirht:"",
			errors:{}
		};
	}
	onChange = e=>{
		this.setState({[e.target.id]:e.target.value});
	};
	onSubmit = e =>{
		e.preventDefault();
		const newUser = {
			name: this.state.name,
			email: this.state.email,
			password: this.state.password,
			bio: this.state.bio,
			jobtitle: this.state.jobtitle,
			techdomain: this.state.techdomain,
			gender: this.state.gender,
			dateofbirth: this.state.dateofbrith
		};
		console.log(newUser);
	}
	render(){
		return (
		<div className="row white">
			<form className="form col s6" onSubmit={this.onSubmit}>
				<div className="row">
					<div className="input-field col s8">
						<input placeholder="Enter Your Full Name"
						id="name" type="text" 
						className="validate black-text"
						onChange = {this.onChange}
						value={this.state.name}
						error={errors.name} />
						<label htmlFor="name active">Name</label>
					</div>
				</div>
				<div className="row">
					<div className="input-field col s8">
						<input placeholder="Minimum 6 Digits" 
						id="password" type="password" 
						className="validate black-text"
						onChange = {this.onChange}
						value={this.state.password}
						error={errors.password} />
						<label htmlFor="password">Password</label>
					</div>
				</div>
				<div className="row">
					<div className="input-field col s8">
						<input placeholder="Enter Your valid EmailID"
						 id="email" type="email" 
						 className="validate black-text"
						 onChange = {this.onChange}
						 value={this.state.email}
						 error={errors.email} />
						<label htmlFor="email">Email</label>
					</div>
				</div>

				<div className="row">
					<div className="input-field col s8">
						<textarea id="bio" 
						className="materialize-textarea black-text"
						onChange = {this.onChange}
						value={this.state.bio}
						error={errors.bio} ></textarea>
						<label htmlFor="bio">Bio</label>
					</div>
				</div>

				<div className="row">
					<div className="input-field col s8">
						<input placeholder="Enter Your Current Job Position"
						id="jobtitle" type="text"
						className="validate black-text"
						onChange = {this.onChange}
						value={this.state.jobtitle}
						error={errors.jobtitle} />
						<label htmlFor="jobtitle">Job Title</label>
					</div>
				</div>


				<div className="row">
					<div className="input-field col s8">
						<input placeholder="Enter Technologies You work on"
						id="domain" type="text"
						className="validate black-text" 
						onChange = {this.onChange}
						value={this.state.techdomain}
						error={errors.techdomain} />
						<label htmlFor="domain black">Technology Domain</label>
					</div>
				</div>


				<div className="row">
					<div className="input-field col s8">
						<select className="black-text"
												 onChange = {this.onChange}
												 value={this.state.gender}
												 error={errors.gender} >
							<option className="black-text" value="" disabled selected>Choose your option</option>
							<option className="black-text" id="male" value="Male">Male</option>
							<option className="black-text" id="female" value="Female">Female</option>
							<option className="black-text" id="others" value="Others">Others</option>
						</select>
						<label htmlFor="gender">Gender</label>
					</div>
				</div>
				<div className="row">
					<div className="input-field col s8">
						<input id="dateofbirth" type="date" 
						className="validate black-text"
						onChange = {this.onChange}
						value={this.state.dateofbirth}
						error={errors.dateofbirth}  />
						<label htmlFor="dob">Date of Birth</label>
					</div>
				</div>
				<button className="btn btn-left orange darken-2">SIGNUP</button>
			</form>
			<div>

			<img src={signup2} alt="signup" className="signup-image" />
			</div>
		</div>

	);
}
}

export default Signup;
